import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import SinglePost from './SinglePost';
import './App.css';
import MenuBar from './MenuBar';
import Header from './Header';
import Footer from './Footer';

function App() {
  const [backendData, setBackendData] = useState([]);

  useEffect(() => {
    fetch('/posts')
      .then(response => response.json())
      .then(data => {
        setBackendData(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const firstThreePosts = backendData.slice(0, 3);
  const relatedPosts = backendData.slice(3);

  return (

    <Router>
      <div>
      <Header/>
        <Routes>
          <Route path="/post/:id" element={<SinglePost />} />
          <Route 
            path="/" 
            element={

              <div>

                <h1>Top 3 Posts</h1>
                <div className="top-three-posts">
                  {firstThreePosts.length === 0 ? (
                    <p>Loading...</p>
                  ) : (
                    <>
                      <div className="big-post">
                        <h3>{firstThreePosts[0].title}</h3>
                        <p>{firstThreePosts[0].body}</p>
                        {firstThreePosts[0].photoUrl && <img src={firstThreePosts[0].photoUrl} alt={firstThreePosts[0].title} />}
                      </div>
                      <div className="small-posts">
                        {firstThreePosts.slice(1).map((post) => (
                          <div key={post.id} className="small-post">
                            <h4>{post.title}</h4>
                            <p>{post.body}</p>
                            {post.photoUrl && <img src={post.photoUrl} alt={post.title} />}
                          </div>
                        ))}
                      </div>
                    </>
                  )}
                </div>

                <h2>Related Posts/Photos</h2>
                <div className="related-posts">
                  {relatedPosts.length === 0 ? (
                    <p>No related posts available.</p>
                  ) : (
                    relatedPosts.map((post) => (
                      <div key={post.id} className="related-post">
                        <Link to={`/post/${post.id}`}>
                          {post.photoUrl && <img src={post.photoUrl} alt={post.title} />}
                          <h4>{post.title}</h4>
                          <p>User ID: {post.userId}</p>
                        </Link>
                      </div>
                    ))
                  )}
                </div>
                <Footer/>
              </div>
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
